 (() => {
  // 1️⃣ Produktdaten als Objekte
  const products = [
    {
      id: 1,
      name: "Beamer Set Große Turnhalle",
      description: "Komplettpaket mit 5 Field Beamern zur vollflächigen Projektion in großen Dreifachturnhallen.",
      price: 19999.99,
      image: "https://encrypted-tbn1.gstatic.com/shopping?q=tbn:ANd9GcREtZdpd801RLtNC45Rj28tUV9odlMnTg1Yix0DADoV_YZ_7N0iVVnVwTl4hQWETgVJSzIRZnQBwPgF14rgHZ3iZ3cdSR5RzSBTkgE54Z5T",
      variants: [],
    },
    {
      id: 2,
      name: 'Beamer Set "Kleine Turnhalle"',
      description: "Beinhaltet 2 Field Beamer für Einfeldturnhallen. Projektion und Steuerung über App.",
      price: 9499.0,
      image: "https://encrypted-tbn3.gstatic.com/shopping?q=tbn:ANd9GcSOOZW_OmFE6aU7ohq4wq-UuYixV044_3bGzfHiYLOZU2hVHOb8Oa_9ltfn7gldv5iTVDK8NhUUE_fTQYAAk4obAhVqVroWRfUnk0vxoatzjO4o7EJaxqkSsw",
      variants: [],
    },
    {
      id: 3,
      name: "Tragetasche",
      description: "Gepolsterte Tragetasche für den sicheren Transport eines Field Beamers.",
      price: 119.0,
      image: "https://encrypted-tbn0.gstatic.com/shopping?q=tbn:ANd9GcQngMaX__0Lxe9edbZf4SAWEHvUtPIPH0IMufLuvQFkBHxkoEqLxH-H4wuh-qp_p13YyP8HesCwlPOo7g_7uHbOlu_jpBtU0bK-QYzIAO8CVB8Tex4If6-83ecAHqiU7YnCdJ425DjB1w&usqp=CAc",
      variants: [],
    },
    {
      id: 4,
      name: "App-Abo (monatlich)",
      description: "Monatliche Nutzungslizenz der Smart Sports App inkl. Updates & Support.",
      price: 29.99,
      image: "data:image/jpeg;base64,",
      variants: [],
    },
    {
      id: 5,
      name: "Ersatzlampe für Field Beamer",
      description: "Hochwertige Austauschlampe für lange Lebensdauer und intensive Nutzung.",
      price: 229.0,
      image: "data:image/jpeg;base64,",
      variants: [],
    },
    {
      id: 6,
      name: "Deckenhalterung mit Schnellverschluss",
      description: "Flexible Montagehalterung für die Decke mit sicherem Schnellverschluss.",
      price: 349.0,
      image: "https://encrypted-tbn3.gstatic.com/shopping?q=tbn:ANd9GcTLkSgczqH6k3ZwFFcwcxJmbJ6QVB8PBcbwxY9tofThj4FJBTFXVnbdp-vJ2X5NP0HvBgAmwss8NgS4qRIwLpG3IUz-IDKXRA&usqp=CAc",
      variants: [],
    },
    {
      id: 7,
      name: "Lade- und Synchronstation",
      description: "Station für gleichzeitiges Aufladen und Konfigurieren von bis zu 5 Field Beamern.",
      price: 799.0,
      image: "data:image/jpeg;base64,",
      variants: [],
    },
    {
      id: 8,
      name: "Schutzkoffer (wasserdicht)",
      description: "Stoß- und wasserfester Hartschalenkoffer für Transport und Lagerung.",
      price: 239.0,
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRsElvicQJJRaOL9XEdf82W4oupp0GYKshE9Q&s",
      variants: [],
    },
    {
      id: 9,
      name: "Signalverstärker für große Hallen",
      description: "Verstärkt WLAN und Bluetooth für stabile App-Steuerung über große Entfernungen.",
      price: 159.0,
      image: "https://images.celexongroup.com/images/D/1380/1/1000019870/de/purelink/Purelink-PT-R-HD20-HDMI-Repeater-Signalverstaerker.jpg",
      variants: [],
    },
    {
      id: 10,
      name: "Basis-Zubehörpaket",
      description: "5 Stromkabel, 5 HDMI-Kabel, Deckenhaken & Kabelbinder. Ideal zur Erstinstallation.",
      price: 399.0,
      image: "https://sdmntprsouthcentralus.oaiusercontent.com/files/00000000-8874-61f7-8037-17c799a07db4/raw?se=2025-06-09T14%3A14%3A17Z&sp=r&sv=2024-08-04&sr=b&scid=f75f6c38-3c04-5ef6-9545-507419240c18&skoid=b32d65cd-c8f1-46fb-90df-c208671889d4&sktid=a48cca56-e6da-484e-a814-9c849652bcb3&skt=2025-06-09T06%3A32%3A59Z&ske=2025-06-10T06%3A32%3A59Z&sks=b&skv=2024-08-04&sig=4iaMkkCPiUtToplkVot/1iyIdJx%2Btv9F3/XdH6uHQBc%3D",
      variants: [],
    },
    {
      id: 11,
      name: "Premium-Zubehörpaket",
      description: "Enthält das Basis-Paket plus Ersatzlampe, Repeater und 1 Monat App-Abo.",
      price: 749.0,
      image: "https://sdmntprwestus2.oaiusercontent.com/files/00000000-eaf4-61f8-a42c-c7cd6bb70b8c/raw?se=2025-06-09T14%3A15%3A59Z&sp=r&sv=2024-08-04&sr=b&scid=a23f5696-371f-533f-9c3b-6f0273ab078c&skoid=b32d65cd-c8f1-46fb-90df-c208671889d4&sktid=a48cca56-e6da-484e-a814-9c849652bcb3&skt=2025-06-09T09%3A24%3A32Z&ske=2025-06-10T09%3A24%3A32Z&sks=b&skv=2024-08-04&sig=Xla8AVyiO7Mc94CRtsKJ7nYUgQ8nCjZgmZcoMzFQ1xA%3D",
      variants: [],
    },
  ];

  // 2️⃣ State
  let cart = JSON.parse(localStorage.getItem("cart")) || [];
  let appliedDiscount = 0;

  // 3️⃣ DOM-Elemente
  const productsGrid = document.getElementById("productsGrid");
  const cartItems = document.getElementById("cartItems");
  const subtotalEl = document.getElementById("subtotal");
  const taxEl = document.getElementById("tax");
  const shippingEl = document.getElementById("shipping");
  const discountAmountEl = document.getElementById("discountAmount");
  const totalEl = document.getElementById("total");

  const discountInput = document.getElementById("discountCode");
  const applyDiscountBtn = document.getElementById("applyDiscountBtn");
  const discountMessage = document.getElementById("discountMessage");

  const checkoutBtn = document.getElementById("checkoutBtn");
  const checkoutSection = document.getElementById("checkoutSection");
  const checkoutForm = document.getElementById("checkoutForm");
  const orderReviewSection = document.getElementById("orderReviewSection");
  const orderSummary = document.getElementById("orderSummary");
  const submitOrderBtn = document.getElementById("submitOrderBtn");
  const cancelOrderBtn = document.getElementById("cancelOrderBtn");

  const orderSuccessSection = document.getElementById("orderSuccessSection");

  const searchInput = document.getElementById("searchInput");

  // 4️⃣ Produkte rendern (mit Suchfilter)
  function renderProducts(filter = "") {
    productsGrid.innerHTML = "";
    const filterLower = filter.toLowerCase();
    const filtered = products.filter(
      (p) =>
        p.name.toLowerCase().includes(filterLower) ||
        p.description.toLowerCase().includes(filterLower)
    );

    if (filtered.length === 0) {
      productsGrid.innerHTML = "<p>Keine Produkte gefunden.</p>";
      return;
    }

    filtered.forEach((product) => {
      const card = document.createElement("div");
      card.className = "product-card";

      let variantSelect = "";
      if (product.variants.length > 0) {
        variantSelect = `<select aria-label="Variante wählen">` +
          product.variants
            .map(
              (v) =>
                `<option value="${v}">${v}</option>`
            )
            .join("") +
          `</select>`;
      }

      card.innerHTML = `
        <img src="${product.image}" alt="${product.name}" />
        <div class="product-info">
          <h3>${product.name}</h3>
          <p>${product.description}</p>
          ${variantSelect}
          <p class="price">${product.price.toFixed(2)} €</p>
          <button class="add-to-cart-btn" data-id="${product.id}">In den Warenkorb</button>
        </div>
      `;

      const btn = card.querySelector("button");
      btn.addEventListener("click", () => {
        const variant = card.querySelector("select")?.value || null;
        addToCart(product.id, variant);
      });

      productsGrid.appendChild(card);
    });
  }

  // 5️⃣ In Warenkorb hinzufügen
  function addToCart(productId, variant) {
    const index = cart.findIndex(
      (item) => item.id === productId && item.variant === variant
    );
    if (index > -1) {
      cart[index].qty++;
    } else {
      cart.push({ id: productId, variant: variant, qty: 1 });
    }
    saveCart();
    renderCart();
  }

  // 6️⃣ Warenkorb speichern
  function saveCart() {
    localStorage.setItem("cart", JSON.stringify(cart));
  }

  // 7️⃣ Warenkorb rendern
  function renderCart() {
    cartItems.innerHTML = "";
    if (cart.length === 0) {
      cartItems.innerHTML = "<p>Ihr Warenkorb ist leer.</p>";
      subtotalEl.textContent = "0.00 €";
      taxEl.textContent = "0.00 €";
      shippingEl.textContent = "0.00 €";
      discountAmountEl.textContent = "0.00 €";
      totalEl.textContent = "0.00 €";
      return;
    }

    cart.forEach((item, idx) => {
      const product = products.find((p) => p.id === item.id);
      if (!product) return;

      const variantText = item.variant ? ` (${item.variant})` : "";
      const itemEl = document.createElement("div");
      itemEl.className = "cart-item";

      itemEl.innerHTML = `
        <div class="cart-item-details">
          <p><strong>${product.name}${variantText}</strong></p>
          <p>${item.qty} × ${product.price.toFixed(2)} €</p>
        </div>
        <div class="cart-item-qty">
          <input type="number" min="1" value="${item.qty}" data-index="${idx}" />
          <button class="cart-item-remove" data-index="${idx}" aria-label="Produkt entfernen">&times;</button>
        </div>
      `;

      const qtyInput = itemEl.querySelector("input");
      qtyInput.addEventListener("change", (e) => {
        let val = parseInt(e.target.value);
        if (isNaN(val) || val < 1) val = 1;
        cart[idx].qty = val;
        saveCart();
        renderCart();
      });

      const removeBtn = itemEl.querySelector(".cart-item-remove");
      removeBtn.addEventListener("click", () => {
        cart.splice(idx, 1);
        saveCart();
        renderCart();
      });

      cartItems.appendChild(itemEl);
    });

    calculateTotals();
  }

  // 8️⃣ Summen berechnen
  function calculateTotals() {
    const subtotal = cart.reduce((sum, item) => {
      const product = products.find((p) => p.id === item.id);
      if (!product) return sum;
      return sum + product.price * item.qty;
    }, 0);

    const tax = subtotal * 0.19; // 19% MwSt
    let shipping = subtotal > 500 ? 0 : 9.99;

    if (subtotal === 0) shipping = 0;

    const discount = appliedDiscount;
    const total = subtotal + tax + shipping - discount;

    subtotalEl.textContent = subtotal.toFixed(2) + " €";
    taxEl.textContent = tax.toFixed(2) + " €";
    shippingEl.textContent = shipping.toFixed(2) + " €";
    discountAmountEl.textContent = discount.toFixed(2) + " €";
    totalEl.textContent = total.toFixed(2) + " €";
  }

  // 9️⃣ Rabattcode anwenden
  applyDiscountBtn.addEventListener("click", () => {
    const code = discountInput.value.trim().toUpperCase();
    if (code === "SMART10") {
      const subtotal = cart.reduce((sum, item) => {
        const product = products.find((p) => p.id === item.id);
        if (!product) return sum;
        return sum + product.price * item.qty;
      }, 0);

      if (subtotal >= 100) {
        appliedDiscount = subtotal * 0.10;
        discountMessage.textContent = "10% Rabatt angewendet!";
      } else {
        discountMessage.textContent = "Mindestbestellwert für Rabatt: 100 €";
        appliedDiscount = 0;
      }
    } else {
      discountMessage.textContent = "Ungültiger Rabattcode";
      appliedDiscount = 0;
    }
    calculateTotals();
  });

  // 10️⃣ Checkout anzeigen
  checkoutBtn.addEventListener("click", () => {
    if (cart.length === 0) {
      alert("Ihr Warenkorb ist leer.");
      return;
    }
    checkoutSection.style.display = "block";
    orderReviewSection.style.display = "none";
    orderSuccessSection.style.display = "none";
  });

  // 11️⃣ Bestellung überprüfen
  checkoutForm.addEventListener("submit", (e) => {
    e.preventDefault();

    const formData = new FormData(checkoutForm);
    const name = formData.get("name").trim();
    const email = formData.get("email").trim();
    const address = formData.get("address").trim();

    if (!name || !email || !address) {
      alert("Bitte füllen Sie alle Pflichtfelder aus.");
      return;
    }

    // Bestellung zusammenstellen
    orderSummary.innerHTML = "";
    cart.forEach((item) => {
      const product = products.find((p) => p.id === item.id);
      const variantText = item.variant ? ` (${item.variant})` : "";
      const li = document.createElement("li");
      li.textContent = `${product.name}${variantText} × ${item.qty} = ${(product.price * item.qty).toFixed(2)} €`;
      orderSummary.appendChild(li);
    });

    const totalPrice = totalEl.textContent;
    const totalLi = document.createElement("li");
    totalLi.innerHTML = `<strong>Gesamtsumme: ${totalPrice}</strong>`;
    orderSummary.appendChild(totalLi);

    checkoutSection.style.display = "none";
    orderReviewSection.style.display = "block";
    orderSuccessSection.style.display = "none";
  });

  // 12️⃣ Bestellung abschicken
  submitOrderBtn.addEventListener("click", () => {
    // Hier könnte AJAX oder API-Call stehen, wir simulieren Erfolg
    cart = [];
    saveCart();
    appliedDiscount = 0;
    discountInput.value = "";
    discountMessage.textContent = "";

    renderCart();
    renderProducts();

    orderReviewSection.style.display = "none";
    orderSuccessSection.style.display = "block";
  });

  // 13️⃣ Bestellung abbrechen
  cancelOrderBtn.addEventListener("click", () => {
    orderReviewSection.style.display = "none";
    checkoutSection.style.display = "block";
  });

  // 14️⃣ Suche (Produktfilter)
  searchInput.addEventListener("input", (e) => {
    renderProducts(e.target.value);
  });

  // 15️⃣ Initialisierung
  renderProducts();
  renderCart();
})();
